const { email, senha } = msg.payload;
if (!email || !senha) {
  node.error("E-mail ou senha não informados");
  msg.res = { statusCode: 400 };
  msg.payload = { sucesso: false, erro: 'E-mail e senha são obrigatórios.' };
  return msg;
}
msg.topic = "SELECT * FROM cadastro WHERE email = ? AND senha = ?";
msg.payload = [ email, senha ];
return msg;
